mapper.select:
select count(id)
from userinfo
where id = #{checkId}

select name, id, email
from userinfo
where id = #{id} and pwd = #{pwd}

select id, pwd, name, email, phoneNm
from userinfo
where id = #{id}

select id, pwd, name, email, phoneNm
from userinfo
order by name;

mapper.update:
update user
name = #{name}, pwd = #{pwd}, email = #{email},
where id = #{id}
